controller.down.onEvent(ControllerButtonEvent.Pressed, function () {
    The_Flying_Saucer.vy = 50
})
sprites.onOverlap(SpriteKind.saucer, SpriteKind.Projectile, function (sprite, otherSprite) {
    info.setLife(0)
    music.wawawawaa.play()
    animation.stopAnimation(animation.AnimationTypes.All, The_Flying_Saucer)
    game.showLongText("Game over! You lose!", DialogLayout.Bottom)
})
scene.onHitWall(SpriteKind.saucer, function (sprite) {
    if (The_Flying_Saucer.isHittingTile(CollisionDirection.Right)) {
        music.baDing.play()
        animation.stopAnimation(animation.AnimationTypes.All, The_Flying_Saucer)
        game.showLongText("Game over! You win! Congratulations!", DialogLayout.Bottom)
    } else {
        music.wawawawaa.play()
        animation.stopAnimation(animation.AnimationTypes.All, The_Flying_Saucer)
        game.showLongText("Game over! You lose!!", DialogLayout.Bottom)
    }
})
controller.right.onEvent(ControllerButtonEvent.Pressed, function () {
    The_Flying_Saucer.setVelocity(50, 0)
})
controller.up.onEvent(ControllerButtonEvent.Pressed, function () {
    The_Flying_Saucer.vy = -50
})
